INSERT INTO `gil_postmeta` VALUES("430", "44", "top_description", "<strong>Kaca melindungi rasa. Ketika kita menikmati makanan atau minuman yang menggunakan kemasan kaca, kita akan merasakan rasa murni dari makanan atau minuman itu, tidak lebih, tidak kurang.</strong>");
